    "input": "organisation_profile:\n      name: \"Lumina Studios\"\n      industry: \"Film & Digital Entertainment\"\n      size: \"2,100 employees\"\n      strategic_goals:\n        - \"Expand global streaming content by 40%\"\n        - \"Use virtual production in 70% of shoots\"\n        - \"Reduce post-production time by 25%\"\n      current_business_challenges:\n        - \"Editors struggling with Unreal Engine pipelines\"\n        - \"Inconsistent use of collaboration tools\"\n        - \"Limited training in AI-assisted VFX workflows\"\n    task_analysis:\n      core_tasks:\n        - \"Operate virtual production stages\"\n        - \"Collaborative editing across global teams\"\n        - \"Apply AI-based VFX enhancements\"\n      required_knowledge_skills:\n        - \"Unreal Engine 5\"\n        - \"Cloud-based collaborative editing\"\n        - \"AI-assisted VFX workflows\"\n      environmental_factors:\n        - \"High-pressure production timelines\"\n        - \"Cross-timezone collaboration\"\n        - \"Diverse tool stacks\"\n      tools_systems_used:\n        - \"Unreal Engine 5\"\n        - \"DaVinci Resolve Studio\"\n        - \"Adobe After Effects (AI plugins)\"\n        - \"Frame.io\"\n    personal_analysis:\n      employee_segments:\n        - role: \"Video Editor\"\n          competency_levels:\n            unreal_engine: \"Low\"\n            collaborative_editing: \"High\"\n            ai_vfx: \"Medium\"\n          performance_gaps:\n            - \"Slow adoption of virtual production\"\n            - \"Inefficient VFX preprocessing\"\n          learning_preferences:\n            - \"Short tutorials\"\n            - \"Interactive samples\"\n        - role: \"VFX Artist\"\n          competency_levels:\n            unreal_engine: \"Medium\"\n            collaborative_editing: \"Low\"\n            ai_vfx: \"High\"\n          performance_gaps:\n            - \"Difficulty with multi-user workflows\"\n            - \"File-handling issues between teams\"\n          learning_preferences:\n            - \"Hands-on masterclasses\"\n            - \"Mentoring sessions\"",
