let currentCompany = 0;

// Display selected company
function displayCompany(index) {
    if (!companiesData || companiesData.length === 0) return;
    
    currentCompany = index;
    const company = companiesData[index];
    
    // Update active tab
    document.querySelectorAll('.tab-button').forEach((btn, i) => {
        btn.classList.toggle('active', i === index);
    });
    
    // Build HTML
    const contentDiv = document.getElementById('company-content');
    
    let html = `
        <h3 class="company-title">${escapeHtml(company.title)}</h3>
        
        <div class="data-section">
            <h4>📋 Input Data</h4>
            <div class="data-content">${escapeHtml(company.input)}</div>
        </div>
        
        <div class="data-section">
            <h4>📊 Analysis Results</h4>
            <div class="output-tabs">
                ${createOutputTabs()}
            </div>
            <div id="output-container">
                ${createOutputContent(company.output_sections)}
            </div>
        </div>
    `;
    
    contentDiv.innerHTML = html;
    attachOutputTabListeners();
}

// Create output tabs HTML
function createOutputTabs() {
    const tabs = [
        { key: 'summary', label: 'Executive Summary' },
        { key: 'organization', label: 'Organization' },
        { key: 'task', label: 'Task Analysis' },
        { key: 'person', label: 'Person Analysis' },
        { key: 'industry', label: 'Industry Context' },
        { key: 'final', label: 'Final Report' }
    ];
    
    return tabs.map((tab, idx) => 
        `<button class="output-tab ${idx === 0 ? 'active' : ''}" data-tab="${tab.key}">
            ${tab.label}
        </button>`
    ).join('');
}

// Create output content HTML - SHOWS ALL CONTENT
function createOutputContent(sections) {
    const tabs = ['summary', 'organization', 'task', 'person', 'industry', 'final'];
    
    return tabs.map((key, idx) => {
        const content = sections[key] || '<p style="color: #6c757d; padding: 2rem;">Content not available for this section.</p>';
        const isActive = idx === 0 ? 'active' : '';
        
        return `
            <div class="output-content ${isActive}" data-tab="${key}">
                <div class="data-content">${escapeHtml(content)}</div>
            </div>
        `;
    }).join('');
}

// Attach listeners to output tabs
function attachOutputTabListeners() {
    document.querySelectorAll('.output-tab').forEach(btn => {
        btn.addEventListener('click', function() {
            const tabKey = this.getAttribute('data-tab');
            
            // Update active tab
            document.querySelectorAll('.output-tab').forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            
            // Show corresponding content
            document.querySelectorAll('.output-content').forEach(content => {
                content.classList.remove('active');
            });
            document.querySelector(`.output-content[data-tab="${tabKey}"]`).classList.add('active');
        });
    });
}

// Copy BibTeX to clipboard
function copyBibtex() {
    const bibtexCode = document.getElementById('bibtex-code').textContent;
    
    navigator.clipboard.writeText(bibtexCode).then(() => {
        const btn = document.querySelector('.copy-btn');
        const originalText = btn.innerHTML;
        
        btn.innerHTML = `
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                <polyline points="20 6 9 17 4 12"></polyline>
            </svg>
            Copied!
        `;
        btn.classList.add('copied');
        
        setTimeout(() => {
            btn.innerHTML = originalText;
            btn.classList.remove('copied');
        }, 2000);
    }).catch(err => {
        console.error('Failed to copy:', err);
        alert('Failed to copy to clipboard');
    });
}

// Escape HTML to prevent XSS
function escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
}

// Initialize on page load
document.addEventListener('DOMContentLoaded', () => {
    // Company tab buttons
    document.querySelectorAll('.tab-button').forEach((btn, index) => {
        btn.addEventListener('click', () => displayCompany(index));
    });
    
    // Load first company (Pharmaceutical)
    displayCompany(0);
    
    // Smooth scroll for anchor links
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            const target = document.querySelector(this.getAttribute('href'));
            if (target) {
                target.scrollIntoView({ behavior: 'smooth', block: 'start' });
            }
        });
    });
});

// Make copyBibtex globally available
window.copyBibtex = copyBibtex;
